    /**
     * Creates an object of {@link SignUpCoordinator} to check the user's input.
     *
     * @param evt
     */
    private void signUpBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signUpBtnActionPerformed

        SignUpCoordinator sCoordinator = new SignUpCoordinator();
        if (!sCoordinator.isAlphabetic(nameField.getText())) {
            JOptionPane.showMessageDialog(this, "Invalid Name", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (passField.getPassword().length == 0) {
            JOptionPane.showMessageDialog(this, "Empty Password", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (confPassField.getPassword().length == 0) {
            JOptionPane.showMessageDialog(this, "Empty Confirm Password", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (!sCoordinator.passMatches(passField.getPassword(), confPassField.getPassword())) {
            JOptionPane.showMessageDialog(this, "Passwords Don't Match", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (!sCoordinator.isPhoneNum(phoneField.getText())) {
            JOptionPane.showMessageDialog(this, "Invalid Phone Number", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (!sCoordinator.isEmail(emailField.getText())) {
            JOptionPane.showMessageDialog(this, "Invalid Email", "Error", JOptionPane.ERROR_MESSAGE);
        } else {
            String name = nameField.getText();
            String password = sCoordinator.charArrayToString(passField.getPassword());
            String phoneNum = phoneField.getText();
            String email = emailField.getText();
            sCoordinator.register(name, password, phoneNum, email);
            JOptionPane.showMessageDialog(this, "You've registered successfully, Your Account Number is: " + sCoordinator.getAccountNum());
            this.dispose();
            new SignInPage().setVisible(true);
        }
    }//GEN-LAST:event_signUpBtnActionPerformed

